mapper.insert:
INSERT IGNORE INTO inventory_deduct_log (
order_id, sku_id, qty, status, create_time, update_time
) VALUES (
#{orderId}, #{skuId}, #{qty}, #{status}, NOW(), NOW()
)

mapper.select:
SELECT order_id, sku_id, qty,status, create_time, update_time
FROM inventory_deduct_log
WHERE order_id = #{orderId} and sku_id = #{skuId}

mapper.update:
update inventory_deduct_log
set status = #{status}
where order_id = #{orderId} and sku_id = #{skuId} and status = 'LOCKED'
